import "./App.css";
import styled from "styled-components";
import { useAppSelector, useAppDispatch } from "./hooks";
import { addTimestamp, addTodo } from "./slices/todo";
import { useState } from "react";
import { useGetTodoListQuery } from "./services/todoApi";

const Wrapper = styled.div`
  padding: 1.5rem;
`;

const AA = styled.p`
  color: blue;
`;

const Title = styled.h2`
  font-weight: 900;
  margin-top: 2rem;
`;

const NoteInput = styled.input`
  width: 100%;
  height: 40px;
  border-radius: 10px;
  padding-left: 0.5rem;
  box-sizing: border-box;
`;

const SubmitBtn = styled.button`
  width: 100%;
  box-sizing: border-box;
  height: 40px;
  background: #000;
  display: flex;
  justify-content: center;
  align-items: center;
  color: #fff;
  border-radius: 10px;
  border: 0;
  font-weight: 900;
  margin-top: 1rem;

  :active {
    background: #000000be;
  }
`;

const Item = styled.div`
  margin-top: 1rem;

  > b {
    margin-right: 0.5rem;
  }
`;

function App() {
  const todoReducer = useAppSelector((state) => state.todoReducer);
  const todoList = todoReducer.todoList;
  const [text, setText] = useState("");
  const dispatch = useAppDispatch();
  const { data, error, isLoading } = useGetTodoListQuery("1");

  console.log("data", data);
  console.log("error", error);
  console.log("isLoading", isLoading);

  return (
    <Wrapper>
      <Title>TODO LIST</Title>

      <AA>djkddk</AA>
      <NoteInput
        value={text}
        type="text"
        onChange={(e: React.ChangeEvent<HTMLInputElement>) => {
          setText(e.target.value);
        }}
      />
      <SubmitBtn
        onClick={() => {
          if (text === "") {
            alert("請輸入TODO內容");
            return;
          }

          dispatch(addTodo(text));
          setText("");
        }}
      >
        Submit
      </SubmitBtn>
      <SubmitBtn
        onClick={() => {
          dispatch(addTimestamp());
        }}
      >
        Record Timestamp
      </SubmitBtn>
      <Title>List</Title>
      {todoList.map((data, index) => {
        return (
          <Item key={data}>
            <b>{index + 1}</b>
            {data}
          </Item>
        );
      })}
      <Title>List2</Title>
      {isLoading && <p>isLoading</p>}
    </Wrapper>
  );
}

export default App;
